#!flask/bin/python
from flask import Flask, jsonify, abort
from flask import make_response

app = Flask(__name__)

plugins = [
    {
        'id': 1,
        'name': u'Souschef',
        'description': u'Convert your plain text recipes to interactive recipes',
        'apk_location': u'http://aurora-files.ml/javadoc/deploy/souschef.apk',
        'logo':u''
    },
    {
        'id': 2,
        'name': u'Paperviewer',
        'description': u'Read papers in a fun and interactive way',
        'apk_location': u'http://aurora-files.ml/javadoc/deploy/paperviewer.apk',
        'logo':u''
        }
]


@app.route('/plugins', methods=['GET'])
def get_plugins():
    return jsonify({'plugins': plugins})


@app.route('/plugins/<int:plugin_id>', methods=['GET'])
def get_task(plugin_id):
    plugin = [plugin for plugin in plugins if plugin['id'] == plugin_id]
    if len(plugin) == 0:
        abort(404)
    return jsonify({'plugin': plugin[0]})


@app.errorhandler(404)
def not_found(error):
    return make_response(jsonify({'error': 'Not found'}), 404)


if __name__ == '__main__':
    app.run(debug=True)
